@trace_mcp_operation
def _hash_file(file_path):
    """Generate hash for a file to use as cache key"""
    hasher = hashlib.md5()
    with open(file_path, 'rb') as f:
        buf = f.read()
        hasher.update(buf)
    return hasher.hexdigest()
